ON materiais_estudados(user_id, progresso, ultima_visualizacao);

-- 7. Daily Summaries: busca por usuário + data (getSummary, getDailyStats)
CREATE INDEX IF NOT EXISTS idx_daily_summaries_user_date 
ON daily_summaries(user_id, date);

-- 8. Estatísticas Diárias: busca por usuário + data (getProgressoSemanal)
CREATE INDEX IF NOT EXISTS idx_estatisticas_diarias_user_data 
ON estatisticas_diarias(user_id, data);

-- 9. Widget Configs: busca por usuário + tipo (updateWidgetConfig)
CREATE INDEX IF NOT EXISTS idx_widget_configs_user_type 
ON widget_configs(user_id, widget_type);

-- 10. Assinaturas: busca por usuário + status (getPlanoAtual)
CREATE INDEX IF NOT EXISTS idx_assinaturas_user_status 
ON assinaturas(user_id, status);

