-- 1. Metas: busca por usuário + prazo + status (getCronograma)
CREATE INDEX IF NOT EXISTS idx_metas_user_prazo_concluida 
ON metas(userId, prazo, concluida);

-- 2. Streak Logs: busca por usuário + data (getCurrentStreak, getHistory)
CREATE INDEX IF NOT EXISTS idx_streak_logs_user_date 
ON streak_logs(userId, date DESC);

-- 3. Telemetry Events: análise por usuário + timestamp
CREATE INDEX IF NOT EXISTS idx_telemetry_events_user_timestamp 
ON telemetry_events(userId, timestamp DESC);

